package minecraft

import (
	"bufio"
	"bytes"
	"encoding/binary"
	"encoding/json"
	"errors"
	"io"
	"net"
	"strconv"
	"strings"
)

const (
	protocolVersion = 0x47
)

type Pong struct {
	Version struct {
		Name     string `json:"name"`
		Protocol int    `json:"protocol"`
	} `json:"version"`
	Players struct {
		Max    int `json:"max"`
		Online int `json:"online"`
		Sample []struct {
			Name string `json:"name"`
			ID   string `json:"id"`
		} `json:"sample"`
	} `json:"players"`
	Description struct {
		Text      string `json:"text"`
		Translate string `json:"translate"`
		Des       string `json:"-"`
	} `json:"description,omitempty"`
	FavIcon string `json:"favicon"`
	ModInfo struct {
		ModType string `json:"type"`
		ModList []struct {
			ModID      string `json:"modid"`
			ModVersion string `json:"version"`
		} `json:"modList"`
	} `json:"modinfo"`
}

func Ping(host string) (*Pong, error) {
	if !strings.Contains(host, ":") {
		host += ":25565"
	}
	conn, err := net.Dial("tcp", host)
	if err != nil {
		return nil, err
	}
	defer conn.Close()
	if err := sendHandshake(conn, host); err != nil {
		return nil, err
	}
	if err := sendStatusRequest(conn); err != nil {
		return nil, err
	}
	p, err := readPong(conn)
	// glgf.Debug(Pong.FavIcon)
	if err != nil {
		// glgf.Error(err)
		return nil, err
	}
	if p.FavIcon == "" {
		p.FavIcon = "data:image/png;base64,"
	}
	if p.Description.Text != "" {
		p.Description.Des = p.Description.Text
	} else {
		p.Description.Des = p.Description.Translate
	}
	return p, nil
}

func makePacket(pl *bytes.Buffer) *bytes.Buffer {
	var buf bytes.Buffer
	// get payload length
	buf.Write(encodeVarint(uint64(len(pl.Bytes()))))
	// write payload
	buf.Write(pl.Bytes())
	return &buf
}

func sendHandshake(conn net.Conn, host string) error {
	pl := &bytes.Buffer{}

	// packet id
	pl.WriteByte(0x00)

	// protocol version
	pl.WriteByte(protocolVersion)

	// server address
	host, port, err := net.SplitHostPort(host)
	if err != nil {
		panic(err)
	}

	pl.Write(encodeVarint(uint64(len(host))))
	pl.WriteString(host)

	// server port
	iPort, err := strconv.Atoi(port)
	if err != nil {
		panic(err)
	}
	_ = binary.Write(pl, binary.BigEndian, int16(iPort))
	// next state (status)
	pl.WriteByte(0x01)
	if _, err := makePacket(pl).WriteTo(conn); err != nil {
		return errors.New("cannot write handshake")
	}

	return nil
}

func sendStatusRequest(conn net.Conn) error {
	pl := &bytes.Buffer{}

	// send request zero
	pl.WriteByte(0x00)

	if _, err := makePacket(pl).WriteTo(conn); err != nil {
		return errors.New("cannot write send status request")
	}

	return nil
}

func encodeVarint(x uint64) []byte {
	var buf [10]byte
	var n int
	for n = 0; x > 127; n++ {
		buf[n] = 0x80 | uint8(x&0x7F)
		x >>= 7
	}
	buf[n] = uint8(x)
	n++
	return buf[0:n]
}

func readPong(rd io.Reader) (*Pong, error) {
	r := bufio.NewReader(rd)
	nl, err := binary.ReadUvarint(r)
	if err != nil {
		return nil, err
	}

	pl := make([]byte, nl)
	_, err = io.ReadFull(r, pl)
	if err != nil {
		return nil, err
	}

	// packet id
	_, n := binary.Uvarint(pl)
	if n <= 0 {
		return nil, errors.New("could not read packet id")
	}

	// string varint
	_, n2 := binary.Uvarint(pl[n:])
	if n2 <= 0 {
		return nil, errors.New("could not read string varint")
	}
	// glgf.Debug(string(pl[n+n2:]))
	var pong Pong
	err = json.Unmarshal(pl[n+n2:], &pong)
	if err != nil {
		dec := make(map[string]interface{})
		_ = json.Unmarshal(pl[n+n2:], &dec)
		pong.Description.Translate, _ = dec["description"].(string)
	}
	return &pong, nil
}
